import React from "react";
import Button from "./Button";

export default class SigninForm extends React.Component {
  constructor() {
    super();
    this.state = {};
    this.signInFunction = this.signInFunction.bind(this);
  }
  signInFunction = () => {
    const emailInputField = document.getElementById("emailInputField").value;
    const passwordInputField = document.getElementById("emailInputField").value;
    const repPasswordInputField = document.getElementById("emailInputField")
      .value;
    // Zmienna, która sprawdza czy mail zawiera znak @
    const emailValid = emailInputField.includes("@");
    const passwordMatch =
      passwordInputField === repPasswordInputField ? true : false;
    if (emailValid && passwordMatch) {
      alert("all good");
    } else {
      alert(
        "Email must contain @ symbol and password must match with each other"
      );
    }
  };

  render() {
    return (
      <div>
        <input
          id="emailInputField"
          className="inputField"
          type="email"
          placeholder="Email"
        ></input>
        <input
          id="passwordInputField"
          className="inputField"
          type="password"
          placeholder="Password"
        ></input>
        <input
          id="repPasswordInputField"
          className="inputField"
          type="password"
          placeholder="Re-Enter Password"
        ></input>
        <div onClick={this.signInFunction}>
          <Button>Sign In</Button>
        </div>
      </div>
    );
  }
}
