import React from "react";
import "./App.css";
import Button from "./Components/Button";
import SigninForm from "./Components/SigninForm";
import Dashboard from "./Components/Dashboard";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

let isLogged;
let userData;

// Funkcja Logująca i pobierająca dane z bazy danych
const loginFunction = () => {
  const emailFieldValue = document.getElementById("emailField").value;
  const passwordFieldValue = document.getElementById("passwordField").value;
  const passwordValid = !passwordFieldValue.includes("%");

  if (passwordValid !== true) {
    alert("Password can't contain % symbol");
  } else {
    const request = new XMLHttpRequest();
    const url = "http://localhost:5000/login";
    const params = `?email=${encodeURIComponent(
      emailFieldValue
    )}&password=${encodeURIComponent(passwordFieldValue)}`;
    const endpoint = url + params;

    // Gdy nadejdzie odpowiedz z serwera
    request.onreadystatechange = () => {
      if (request.readyState === 4) {
        console.log(request.responseText);
      }
    };
    request.open("GET", endpoint);
    request.send();
  }
};

function App() {
  return (
    <div>
      <Router>
        <Switch>
          {/* GŁÓWNA STRONA */}
          <Route path="/" exact>
            {isLogged ? (
              // GDY ZALOGOWANY
              <Dashboard userData={userData}></Dashboard>
            ) : (
              // GDY NIEZALOGOWANY
              <div>
                <input
                  id="emailField"
                  className="inputField"
                  type="email"
                  placeholder="Email"
                ></input>
                <input
                  id="passwordField"
                  className="inputField"
                  type="password"
                  placeholder="Password"
                ></input>
                <div onClick={loginFunction}>
                  <Button>Log in</Button>
                </div>

                <Link to="/signin">
                  <div id="signInAnchor">Sign In</div>
                </Link>
              </div>
            )}
          </Route>

          {/* EKRAN Z WYBOREM LOGOWANIA LUB REJESTRACJI */}
          {/* WYRENDEROWANIE FORMULARZA REJESTRACJI */}
          <Route path="/signin" component={SigninForm}></Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
