import React from "react";
import "./App.css";
import Button from "./Components/Button";
import SigninForm from "./Components/SigninForm";
import Dashboard from "./Components/Dashboard";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

let isLogged = false;
let userData = null;

function App() {
  return (
    <div>
      <Router>
        <Switch>
          {/* GŁÓWNA STRONA */}
          <Route path="/" exact>
            {isLogged ? (
              // GDY ZALOGOWANY
              <Dashboard userData={userData}></Dashboard>
            ) : (
              // GDY NIEZALOGOWANY
              <div>
                <form method="POST" action="">
                <input
                  className="inputField"
                  type="email"
                  placeholder="Email"
                ></input>
                <input
                  className="inputField"
                  type="password"
                  placeholder="Password"
                ></input>
                <input type="submit"><Button>Log In</Button></input>
                </form>

                <Link to="/signin">
                  <div id="signInAnchor">Sign In</div>
                </Link>
              </div>
            )}
          </Route>

          {/* EKRAN Z WYBOREM LOGOWANIA LUB REJESTRACJI */}
          {/* WYRENDEROWANIE FORMULARZA REJESTRACJI */}
          <Route path="/signin" component={SigninForm}></Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
